catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
   if (isLoading || newValue == '') {
      return;
   }

   //Type appropriate comment here, and begin script below
    // Call the server-side script include to gather location details
   var ga = new GlideAjax('gatherLocation'); // gatherLocation is the script include class
   ga.addParam('sysparm_name', 'locationDetail'); // locationDetail is the script include method
   ga.addParam('sysparm_user_sysid', g_form.getValue("requested_for")); // Set parameter sysparm_user_sysid to 'requested_for'
   ga.getXML(setLocation);
}

// Callback function for returning the result from the server-side code
function setLocation(response) {
    var answer = response.responseXML.documentElement.getAttribute("answer");
    g_form.setValue('office_location', answer);
}
